import React, { useState, useEffect } from "react";
import "./CheckoutPage.css";
import { useCart } from "../CartContext";
import { useUser } from "../UserContext";

const CheckoutPage = () => {
  const { user, loading } = useUser();
  console.log("Current user:", user);
  const { cartItems, totalAmount, updateQuantity, clearCart } = useCart();
  const [formData, setFormData] = useState({
    name: user?.username || "",
    email: user?.email || "",
    phone: user?.phone_number || "",
  });
  const [errors, setErrors] = useState({});
  const [isSubmitted, setIsSubmitted] = useState(false);

  useEffect(() => {
    if (user) {
      setFormData({
        name: user.username,
        email: user.email,
        phone: user.phone_number,
      });
    }
  }, [user]);
  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) newErrors.name = "Имя обязательно";
    if (!/\S+@\S+\.\S+/.test(formData.email)) newErrors.email = "Неверный email";
    if (!user && !formData.phone.trim()) newErrors.phone = "Телефон обязателен";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      console.log("Order data:", { ...formData, cartItems });
      clearCart();
      setIsSubmitted(true);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleQuantityChange = (id, newQuantity) => {
    if (newQuantity < 1) return;
    updateQuantity(id, newQuantity);
  };

  return (
    <div className="checkout-container">
      <h1 className="checkout-title">Оформление заказа</h1>

      <div className="checkout-content">
        <div className="cart-section">
          <h2>Ваша корзина</h2>
          <div className="cart-items">
            {cartItems.map(item => (
              <div key={item.id} className="cart-item">
                <img 
                  src={item.image_url} 
                  alt={item.title} 
                  className="product-image"
                  loading="lazy"
                />
                <div className="item-details">
                  <h3>{item.title}</h3>
                  <p>{item.price} BYN × {item.quantity}</p>
                  <div className="quantity-controls">
                    <button 
                      onClick={() => handleQuantityChange(item.id, item.quantity - 1)}
                      aria-label="Уменьшить количество"
                    >
                      -
                    </button>
                    <span>{item.quantity}</span>
                    <button 
                      onClick={() => handleQuantityChange(item.id, item.quantity + 1)}
                      aria-label="Увеличить количество"
                    >
                      +
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
          <div className="cart-total">
            <span>Итого:</span>
            <span>{totalAmount} BYN</span>
          </div>
        </div>

        <form className="checkout-form" onSubmit={handleSubmit}>
          <h2>Данные для оформления</h2>

          <div className={`form-group ${errors.name ? "error" : ""}`}>
            <label htmlFor="name">Имя</label>
            <input
              id="name"
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              disabled={!!user}
              aria-invalid={!!errors.name}
            />
            {errors.name && (
              <span className="error-message" role="alert">
                {errors.name}
              </span>
            )}
          </div>

          <div className={`form-group ${errors.email ? "error" : ""}`}>
            <label htmlFor="email">Email</label>
            <input
              id="email"
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              disabled={!!user}
              aria-invalid={!!errors.email}
            />
            {errors.email && (
              <span className="error-message" role="alert">
                {errors.email}
              </span>
            )}
          </div>

          {!user && (
            <div className={`form-group ${errors.phone ? "error" : ""}`}>
              <label htmlFor="phone">Телефон</label>
              <input
                id="phone"
                type="tel"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                placeholder="+375 (XX) XXX-XX-XX"
                aria-invalid={!!errors.phone}
              />
              {errors.phone && (
                <span className="error-message" role="alert">
                  {errors.phone}
                </span>
              )}
            </div>
          )}

          <div className="terms-group">
            <input
              type="checkbox"
              id="terms"
              required
              aria-describedby="terms-description"
            />
            <label htmlFor="terms">
              Я согласен с условиями обработки данных
            </label>
          </div>

          <button 
            type="submit" 
            className="submit-button"
            aria-disabled={isSubmitted}
          >
            Подтвердить заказ
          </button>
        </form>
      </div>

      {isSubmitted && (
        <div className="modalOverlay">
          <div className="modalContent">
            <h2>Спасибо за покупку!</h2>
            <p>Ваш заказ успешно оформлен.</p>
            <button 
              onClick={() => setIsSubmitted(false)}
              className="close-button"
            >
              Закрыть
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CheckoutPage;
