import React, { useContext, useState, useEffect } from "react";
import { FiShoppingCart } from "react-icons/fi";
import { IoCarSportOutline } from "react-icons/io5";
import { CartContext } from "../CartContext";
import axios from "axios";
import './ShopCart.css';

export default function Header() {
  const [cartOpen, setCartOpen] = useState(false);
  const { cartItems, removeFromCart, totalAmount } = useContext(CartContext);
  const [searchTerm, setSearchTerm] = useState(""); // Состояние для поиска
  const [searchResults, setSearchResults] = useState([]); // Найденные товары

  useEffect(() => {
    const fetchSearchResults = async () => {
      if (searchTerm.trim() === "") {
        setSearchResults([]);
        return;
      }

      try {
        const response = await axios.get(`http://localhost:5001/api/products?search=${searchTerm}`);
        setSearchResults(response.data);
      } catch (error) {
        console.error("Ошибка при поиске товаров:", error);
      }
    };

    fetchSearchResults();
  }, [searchTerm]);

  return (
    <header>
      <div>
        <span className="logo">
          Auto Dom <IoCarSportOutline />
        </span>
        <ul className="nav">
          <li>Каталог</li>
          <li>Информация</li>
          <li>Контакты</li>
        </ul>

        {/* Строка поиска */}
        <input
          type="text"
          placeholder="Поиск товаров..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="header-search-bar"
        />
        {searchTerm && (
          <div className="search-results">
            {searchResults.length > 0 ? (
              searchResults.map((product) => (
                <div key={product.id} className="search-item">
                  <img src={product.image_url} alt={product.title} className="search-item-image" />
                  <div>
                    <p className="search-item-title">{product.title}</p>
                    <p className="search-item-price">{product.price} BYN</p>
                  </div>
                  <button
                    className="btn-buy"
                    onClick={() => {
                      alert(`Товар "${product.title}" добавлен в корзину`);
                      setSearchTerm(""); // Очистить строку поиска
                    }}
                  >
                    Купить
                  </button>
                </div>
              ))
            ) : (
              <p className="no-results">Товары не найдены</p>
            )}
          </div>
        )}

        <FiShoppingCart
          onClick={() => setCartOpen(!cartOpen)}
          className={`shop-cart-button ${cartOpen && "active"}`}
        />

        {cartOpen && (
          <div className="shop-cart">
            {cartItems.length > 0 ? (
              <>
                {cartItems.map((item) => (
                  <div key={item.id} className="cart-item">
                    <img src={item.image} alt={item.name} className="cart-item-image" />
                    <div className="cart-item-details">
                      <p>{item.title}</p>
                      <p>
                        {item.quantity} шт. × {item.price} BYN ={" "}
                        {item.quantity * item.price} BYN
                      </p>
                    </div>
                    <button
                      className="remove-item-button"
                      onClick={() => removeFromCart(item.id)}
                    >
                      ✕
                    </button>
                  </div>
                ))}
                <div className="cart-total">
                  <strong>Итого: {totalAmount} BYN</strong>
                </div>
                <button className="view-cart-button">Посмотреть корзину</button>
              </>
            ) : (
              <p className="pusto">Корзина пуста</p>
            )}
          </div>
        )}
      </div>
      <div className="presentation"></div>
    </header>
  );
}
