import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./ProfilePage.css";

function ProfilePage() {
  const [user, setUser] = useState(null);
  const [orders, setOrders] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem("token");
        const headers = { Authorization: `Bearer ${token}` };

        // Получаем данные пользователя
        const userResponse = await axios.get("http://localhost:5001/api/me", { headers });
        setUser(userResponse.data);

        // Получаем историю заказов
        const ordersResponse = await axios.get("http://localhost:5001/api/orders", { headers });
        setOrders(ordersResponse.data);
      } catch (err) {
        console.error("Ошибка при загрузке данных:", err);
        navigate("/login");
      }
    };

    fetchData();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
    window.location.reload();
  };

  if (!user) return <p>Загрузка...</p>;

  return (
    <div className="profile-wrapper">
      <div className="profile-page">
        <h1>Личный кабинет</h1>
        <p><strong>Имя пользователя:</strong> {user.username}</p>
        <p><strong>Email:</strong> {user.email}</p>
        <p><strong>Дата регистрации:</strong> {new Date(user.created_at).toLocaleString()}</p>
        <button className="logout-button" onClick={handleLogout}>Выйти</button>

        <h2>История заказов</h2>
        {orders.length > 0 ? (
          <ul className="order-list">
            {orders.map(order => (
              <li key={order.id} className="order-item">
                <p><strong>Заказ №{order.id}</strong></p>
                <p>Статус: <span className={`status-${order.status}`}>{order.status}</span></p>
                <p>Дата: {new Date(order.created_at).toLocaleString()}</p>
                <p>Сумма: {order.total_amount} BYN</p>
              </li>
            ))}
          </ul>
        ) : (
          <p>У вас еще нет заказов.</p>
        )}
      </div>
    </div>
  );
}

export default ProfilePage;
