import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import './ProfilePage.css';

function ProfilePage() {
  const [user, setUser] = useState(null);
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem("token");
        const userResponse = await axios.get("http://localhost:5001/api/me", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(userResponse.data);

        const ordersResponse = await axios.get("http://localhost:5001/api/orders", {
          headers: { Authorization: `Bearer ${token}` },
        });

        console.log("Ответ от сервера с заказами:", ordersResponse.data);
        setOrders(Array.isArray(ordersResponse.data) ? ordersResponse.data : []);
      } catch (err) {
        console.error("Ошибка загрузки данных:", err);
        if (err.response?.status === 404) {
          setOrders([]);
        } else {
          navigate("/login");
        }
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
    window.location.reload();
  };

  const getStatusColor = (status) => {
    const colors = {
      processing: '#ffd700', // Желтый
      shipped: '#4CAF50', // Зеленый
      delivered: '#2196F3', // Синий
      cancelled: '#f44336' // Красный
    };
    return colors[status] || '#999';
  };

  if (loading) return <div className="loading-spinner"></div>;

  return (
    <div className="profile-container">
      <div className="profile-card">
        <h1>Личный кабинет</h1>
        <div className="user-info">
          <p><span>Имя пользователя:</span> {user.username}</p>
          <p><span>Email:</span> {user.email}</p>
          <p><span>Дата регистрации:</span> {new Date(user.created_at).toLocaleDateString()}</p>
        </div>

        <button className="logout-btn" onClick={handleLogout}>
          Выйти из аккаунта
        </button>
      </div>

      <div className="orders-section">
        <h2>История заказов</h2>
        {orders.length === 0 ? (
          <p className="no-orders">У вас пока нет заказов</p>
        ) : (
          <div className="orders-grid">
            {orders.map(order => (
              <div key={order.id} className="order-card">
                <div className="order-header">
                  <span>Заказ #{order.id}</span>
                  <span className="order-status" style={{ backgroundColor: getStatusColor(order.status) }}>
                    {order.status}
                  </span>
                </div>
                <div className="order-body">
                  <p><span>Дата:</span> {new Date(order.created_at).toLocaleString()}</p>
                  <p><span>Сумма:</span> {order.total_amount} BYN</p>
                  <div className="products-list">
                    <span>Товары:</span>
                    {order.products.map((product, idx) => (
                      <div key={idx} className="product-item">
                        <span>{product.title}</span>
                        <span>{product.quantity} × {product.price} BYN</span>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default ProfilePage;
