import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import "./RegisterPage.css";

function RegisterPage() {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [error, setError] = useState("");
  const [verificationCode, setVerificationCode] = useState("");
  const [isCodeSent, setIsCodeSent] = useState(false);
  const navigate = useNavigate();

  // Функция для валидации номера телефона
  const validatePhoneNumber = (phone) => {
    const regex = /^\+?[0-9]{10,15}$/;
    return regex.test(phone);
  };

  // Отправка данных регистрации и запрос на отправку кода
  const handleRegister = async (e) => {
    e.preventDefault();

    if (!validatePhoneNumber(phoneNumber)) {
      setError("Некорректный номер телефона. Используйте формат +375999999999 или 80299999999.");
      return;
    }

    try {
      // Отправляем запрос на отправку кода подтверждения
      await axios.post("http://localhost:5001/api/send-verification-code", { email });
      setIsCodeSent(true); // Показываем форму для ввода кода
    } catch (err) {
      setError("Ошибка при отправке кода подтверждения.");
    }
  };

  // Проверка кода подтверждения
  const handleVerifyCode = async () => {
    try {
      const response = await axios.post("http://localhost:5001/api/verify-code", {
        email,
        code: verificationCode,
      });

      if (response.status === 200) {
        // Если код верный, завершаем регистрацию
        await axios.post("http://localhost:5001/api/register", {
          username,
          email,
          password,
          phone_number: phoneNumber,
        });
        navigate("/login");
      }
    } catch (err) {
      setError("Неверный код подтверждения.");
    }
  };

  return (
    <div className="auth-page">
      <div className="register-form">
        <h1>Регистрация</h1>
        {!isCodeSent ? (
          <form onSubmit={handleRegister}>
            <input
              type="text"
              placeholder="Имя пользователя"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Пароль"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <input
              type="tel"
              placeholder="+375(XX) XXX-XX-XX"
              value={phoneNumber}
              onChange={(e) => setPhoneNumber(e.target.value)}
              required
            />
            {error && <p className="error">{error}</p>}
            <button type="submit">Зарегистрироваться</button>
          </form>
        ) : (
          <div>
            <p>На вашу почту отправлен код подтверждения. Введите его ниже:</p>
            <input
              type="text"
              placeholder="Код подтверждения"
              value={verificationCode}
              onChange={(e) => setVerificationCode(e.target.value)}
              required
            />
            {error && <p className="error">{error}</p>}
            <button onClick={handleVerifyCode}>Подтвердить</button>
          </div>
        )}
        <p>
          Есть аккаунт? <Link className="link-style" to="/login">Войти</Link>
        </p>
      </div>
    </div>
  );
}

export default RegisterPage;
