import React from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Header from "./components/Header";
import Footer from "./components/Footer";
import HomePage from "./pages/HomePage";
import ModelsPage from "./pages/ModelsPage";
import ProductsPage from "./pages/ProductsPage";
import { CartProvider } from "./CartContext";
import Categories from "./components/Categories";


function App() {


  const [selectedCategory, setSelectedCategory] = useState("all");

  const chooseCategory = (categoryKey) => {
    setSelectedCategory(categoryKey);
  };


  return (
    <CartProvider>
      <Router>
        <div className="wrapper">
          <Header />
          <div>
            <Categories chooseCategory={chooseCategory} />
            <ProductsPage selectedCategory={selectedCategory} />
          </div>
          <Routes>
            {/* Главная страница с марками */}
            <Route path="/" element={<HomePage />} />
            {/* Страница с моделями */}
            <Route path="/brand/:brand" element={<ModelsPage />} />
            {/* Страница с товарами */}
            <Route path="/brand/:brand/model/:model" element={<ProductsPage />} />
          </Routes>
          <Footer />
        </div>
      </Router>
    </CartProvider>
  );
}

export default App;
