import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import './HomePage.css';

function HomePage() {
  const [brands, setBrands] = useState([]);

  useEffect(() => {
    const fetchBrands = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/brands');
        setBrands(response.data);
      } catch (error) {
        console.error("Ошибка при загрузке марок:", error);
      }
    };

    fetchBrands();
  }, []);

  return (
    <div className="homepage">
      <h1 className="title">Марки автомобилей</h1>
      <hr className="polosa" />
      <div className="brand-list">
        {brands.map((brand) => (
          <Link to={`/brand/${brand.name}`} key={brand.id}>
            <div className="brand-item">
              <img src={brand.image_url} className="brand-image" alt={brand.name} />
              <p className="brand-title">{brand.name}</p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
