import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./CheckoutPage.css"; // Создайте файл для стилизации

const CheckoutPage = ({ cartItems, totalAmount }) => {
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!name || !phone || !email) {
      setError("Пожалуйста, заполните все поля.");
      return;
    }
    setError("");
    setSuccessMessage("Спасибо за заказ, мы вам перезвоним!");

    setTimeout(() => {
      navigate("/"); // После подтверждения перенаправляем на главную страницу
    }, 3000);
  };

  return (
    <div className="checkout-page">
      <h1>Оформление заказа</h1>
      <div className="cart-summary">
        {cartItems.map((item) => (
          <div key={item.id} className="cart-item">
            <p>{item.title}</p>
            <p>
              {item.quantity} шт. × {item.price} BYN ={" "}
              {item.quantity * item.price} BYN
            </p>
          </div>
        ))}
        <p className="total-amount">Итого: {totalAmount} BYN</p>
      </div>
      <form onSubmit={handleSubmit} className="checkout-form">
        <input
          type="text"
          placeholder="Имя"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="tel"
          placeholder="Телефон"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        {error && <p className="error">{error}</p>}
        {successMessage && <p className="success">{successMessage}</p>}
        <button type="submit">Оформить заказ</button>
      </form>
    </div>
  );
};

export default CheckoutPage;
