import React, { useEffect, useState } from "react";
import { useParams, useLocation, Link } from "react-router-dom";
import axios from "axios";
import './ModelsPage.css';

function ModelsPage() {
  const { brandId } = useParams(); // Извлекаем brandId из параметров маршрута
  const location = useLocation();
  const brandName = location.state?.brandName || "Неизвестный бренд"; // Получаем имя бренда, если передано

  const [models, setModels] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchModels = async () => {
      try {
        const response = await axios.get(`http://localhost:5001/api/models?brand=${brand}`);
        setModels(response.data);
      } catch (error) {
        console.error("Ошибка при загрузке моделей:", error);
        setError("Не удалось загрузить модели. Попробуйте позже.");
      }
    };

    fetchModels();
  }, [brandId]);

  return (
    <div className="models-page">
      <h1 className="title">Модели {brandName}</h1>
      <hr className="polosa" />
      {error && <p className="error">{error}</p>}
      <div className="model-list">
        {models.length > 0 ? (
          models.map((model) => (
            <Link to={`/brand/${brandId}/model/${model.id}`} key={model.id}>
              <div className="model-item">{model.name}</div>
            </Link>
          ))
        ) : (
          <p>Нет доступных моделей</p>
        )}
      </div>
    </div>
  );
}

export default ModelsPage;
