import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import './Auth.css';

function LoginPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false); // Состояние для управления видимостью модального окна
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5001/api/login", { email, password });
      localStorage.setItem("token", response.data.token); // Сохраняем токен в локальное хранилище
      setIsModalOpen(true); // Показываем модальное окно
      setTimeout(() => {
        setIsModalOpen(false); // Скрываем модальное окно через 2 секунды
        navigate("/"); // Перенаправляем на главную страницу
      }, 2000);
    } catch (err) {
      setError("Неправильный email или пароль");
    }
  };

  return (
    <div className="auth-page">
      <div className="login-form">
        <h1>Войти</h1>
        <form onSubmit={handleLogin}>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Пароль"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          {error && <p className="error">{error}</p>}
          <button type="submit">Войти</button>
        </form>
        <p>
          Нет аккаунта? <Link className="link-style" to="/register">Регистрация</Link>
        </p>
      </div>

      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <p>Авторизация прошла успешно</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default LoginPage;
