import React, { useState, useEffect } from "react";
import "./CheckoutPage.css";
import { useCart } from "../CartContext";
import { useUser } from "../UserContext"; // Предполагается существование контекста пользователя

const CheckoutPage = () => {
  const { user } = useUser(); // Получаем данные пользователя
  const { cartItems, totalAmount, updateQuantity, clearCart } = useCart();
  const [formData, setFormData] = useState({
    name: user?.username || "",
    email: user?.email || "",
    phone: user?.phone_number || "",
  });
  const [errors, setErrors] = useState({});
  const [isSubmitted, setIsSubmitted] = useState(false);

  useEffect(() => {
    if (user) {
      setFormData({
        name: user.username,
        email: user.email,
        phone: user.phone_number,
      });
    }
  }, [user]);

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) newErrors.name = "Имя обязательно";
    if (!/\S+@\S+\.\S+/.test(formData.email)) newErrors.email = "Неверный email";
    if (!user && !formData.phone.trim()) newErrors.phone = "Телефон обязателен";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      console.log("Order data:", { ...formData, cartItems });
      clearCart();
      setIsSubmitted(true);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleQuantityChange = (id, newQuantity) => {
    if (newQuantity < 1) return;
    updateQuantity(id, newQuantity);
  };

  return (
    <div className="checkout-container">
      <h1 className="checkout-title">Оформление заказа</h1>

      <div className="checkout-content">
        {/* Левая колонка - Корзина */}
        <div className="cart-section">
          <h2>Ваша корзина</h2>
          <div className="cart-items">
            {cartItems.map(item => (
              <div key={item.id} className="cart-item">
                <img src={item.image_url} alt={item.title} className="product-image" />
                <div className="item-details">
                  <h3>{item.title}</h3>
                  <p>{item.price} BYN × {item.quantity}</p>
                  <div className="quantity-controls">
                    <button onClick={() => handleQuantityChange(item.id, item.quantity - 1)}>
                      -
                    </button>
                    <span>{item.quantity}</span>
                    <button onClick={() => handleQuantityChange(item.id, item.quantity + 1)}>
                      +
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
          <div className="cart-total">
            <span>Итого:</span>
            <span>{totalAmount} BYN</span>
          </div>
        </div>

        {/* Правая колонка - Форма */}
        <form className="checkout-form" onSubmit={handleSubmit}>
          <div className={`form-group ${errors.name ? "error" : ""}`}>
            <label>Имя</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              disabled={!!user}
            />
            {errors.name && <span className="error-message">{errors.name}</span>}
          </div>

          <div className={`form-group ${errors.email ? "error" : ""}`}>
            <label>Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              disabled={!!user}
            />
            {errors.email && <span className="error-message">{errors.email}</span>}
          </div>

          {!user && (
            <div className={`form-group ${errors.phone ? "error" : ""}`}>
              <label>Телефон</label>
              <input
                type="tel"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                placeholder="+375 (XX) XXX-XX-XX"
              />
              {errors.phone && <span className="error-message">{errors.phone}</span>}
            </div>
          )}

          <div className="terms-group">
            <label>
              <input
                type="checkbox"
                required
              />
              Я согласен с условиями обработки данных
            </label>
          </div>

          <button type="submit" className="submit-button">
            Подтвердить заказ
          </button>
        </form>
      </div>

      {isSubmitted && (
        <div className="thank-you-modal">
          <div className="modal-content">
            <h2>Спасибо за покупку!</h2>
            <p>Ваш заказ успешно оформлен.</p>
            <button onClick={() => setIsSubmitted(false)}>Закрыть</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CheckoutPage;
