import React, { useEffect, useState, useContext } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { CartContext } from "../CartContext";
import './ProductsPage.css';

function ProductsPage() {
  const { engineId } = useParams();
  const [products, setProducts] = useState([]);
  const [quantities, setQuantities] = useState({});
  const { addToCart } = useContext(CartContext);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get(`http://localhost:5001/api/engines/${engineId}/products`);
        setProducts(response.data);
      } catch (error) {
        console.error("Ошибка при загрузке товаров:", error);
      }
    };

    fetchProducts();
  }, [engineId]);

  const incrementQuantity = (productId) => {
    setQuantities(prev => ({
      ...prev,
      [productId]: (prev[productId] || 1) + 1
    }));
  };

  const decrementQuantity = (productId) => {
    setQuantities(prev => ({
      ...prev,
      [productId]: Math.max((prev[productId] || 1) - 1, 1)
    }));
  };

  return (
    <div className="products-page">
      <h1>Товары для двигателя {engineId}</h1>
      <div className="product-list">
        {products.map((product) => (
          <div className="product-card" key={product.id}>
            <img src={product.image_url} alt={product.title} className="product-image" />
            <div className="product-info">
              <p className="product-sku">SKU: {product.sku}</p>
              <div className="title-quantity-wrapper">
                <h2 className="product-title">{product.title}</h2>
                <div className="quantity-control">
                  <button 
                    className="quantity-btn" 
                    onClick={() => decrementQuantity(product.id)}
                  >
                    -
                  </button>
                  <span className="quantity-value">
                    {quantities[product.id] || 1}
                  </span>
                  <button 
                    className="quantity-btn" 
                    onClick={() => incrementQuantity(product.id)}
                  >
                    +
                  </button>
                </div>
              </div>
              <p className="product-price">
                {(product.price * (quantities[product.id] || 1)).toFixed(2)} BYN
              </p>
            </div>
            <div className="product-buttons">
              <button 
                className="btn-buy" 
                onClick={() => addToCart({
                  ...product,
                  quantity: quantities[product.id] || 1
                })}
              >
                Купить
              </button>
              <button className="btn-buy-one-click">Купить в 1 клик</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProductsPage;
