import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import './ModelsPage.css';

function ModelsPage() {
  const { brand } = useParams(); // Извлечение параметра brand из URL
  const [models, setModels] = useState([]);

  useEffect(() => {
    console.log("brand from useParams:", brand); // Для отладки
    if (brand) { // Проверяем, что brand определён
      const fetchModels = async () => {
        try {
          const response = await axios.get(`http://localhost:5001/api/brands/${brand}`);
          setModels(response.data);
        } catch (error) {
          if (error.response && error.response.status === 404) {
            console.error("Модели не найдены для бренда:", brand);
          } else {
            console.error("Ошибка при загрузке моделей:", error);
          }
        }
      };

      fetchModels();
    }
  }, [brand]);

  return (
    <div className="models-page">
      <h1 className="title">Модели</h1>
      <hr className="polosa" />
      <div className="model-list">
        {models.map((model) => (
          <Link to={`/brand/${brand}/model/${model.name}`} key={model.id}>
            <div className="model-item">{model.name}</div>
          </Link>
        ))}
      </div>
    </div>
  );
}

export default ModelsPage;
