import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "./CheckoutPage.css";

const CheckoutPage = () => {
  const navigate = useNavigate();
  const location = useLocation();

  // Получаем данные корзины из location.state
  const { cartItems = [], totalAmount = 0 } = location.state || {};

  // Локальное состояние для формы
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [error, setError] = useState("");

  // Обработка отправки формы
  const handleSubmit = (e) => {
    e.preventDefault();

    if (!name || !phone || !email) {
      setError("Пожалуйста, заполните все поля.");
      return;
    }

    setError("");
    alert("Спасибо за заказ, мы вам перезвоним!");
    navigate("/"); // Перенаправляем на главную страницу после успешного заказа
  };

  return (
    <div className="checkout-page">
      <h1>Оформление заказа</h1>

      <div className="cart-summary">
        <h2>Ваш заказ</h2>
        {cartItems.length > 0 ? (
          cartItems.map((item) => (
            <div key={item.id} className="cart-item">
              <p>{item.title}</p>
              <p>
                {item.quantity} шт. × {item.price} BYN ={" "}
                {item.quantity * item.price} BYN
              </p>
            </div>
          ))
        ) : (
          <p>Корзина пуста</p>
        )}
        <p className="total-amount">Итого: {totalAmount} BYN</p>
      </div>

      <div className="checkout-form">
        <h2>Получатель</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Имя:*</label>
            <input
              type="text"
              id="name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="phone">Телефон:*</label>
            <input
              type="text"
              id="phone"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">E-mail:</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="form-group-checkbox">
            <input type="checkbox" id="agreement" required />
            <label htmlFor="agreement">
              Я согласен на обработку персональных данных
            </label>
          </div>
          {error && <p className="error">{error}</p>}
          <button type="submit" className="submit-button">
            Оформить заказ
          </button>
        </form>
      </div>
    </div>
  );
};

export default CheckoutPage;
