import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import './RegisterPage.css';

function RegisterPage() {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();


  // Функция для валидации номера телефона
  const validatePhoneNumber = (phone) => {
    const regex = /^\+?[0-9]{10,15}$/; // Пример для международных номеров
    return regex.test(phone);
  };

  const handleRegister = async (e) => {
    e.preventDefault();

    // Проверка валидации номера телефона
    if (!validatePhoneNumber(phoneNumber)) {
      setError("Некорректный номер телефона. Используйте формат +375999999999 или 80299999999.");
      return;
    }

    try {
      // Отправляем запрос с username, email, password и phoneNumber
      await axios.post("http://localhost:5001/api/register", {
        username,
        email,
        password,
        phone_number: phoneNumber,
      });
      navigate("/login");
    } catch (err) {
      if (err.response?.status === 409) {
        setError("Email, имя пользователя или номер телефона уже заняты.");
      } else {
        setError("Ошибка при регистрации. Попробуйте снова.");
      }
    }
  };

  return (
    <div className="auth-page">
      <div className="register-form">
        <h1>Регистрация</h1>
        <form onSubmit={handleRegister}>
          <input
            type="text"
            placeholder="Имя пользователя"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Пароль"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <input
            type="tel"
            placeholder="+375 (XX) XXX-XX-XX"
            value={phoneNumber}
            onChange={(e) => setPhoneNumber(e.target.value)}
            required
          />
          {error && <p className="error">{error}</p>}
          <button type="submit">Зарегистрироваться</button>
        </form>
        <p>
          Есть аккаунт? <Link className="link-style" to="/login">Войти</Link>
        </p>
      </div>
    </div>
  );
}

export default RegisterPage;
