import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';

class Categories extends Component {
  constructor(props) {
    super(props);
    this.state = {
      categories: [
        { name: 'Все', path: '/' },
        { name: 'Легковые авто', path: '/brand/cars' },
        { name: 'Мототехника', path: '/brand/motorcycles' },
        { name: 'Грузовики', path: '/brand/trucks' },
      ],
    };
  }

  handleCategoryClick = (path) => {
    this.props.navigate(path); // Переход на указанную страницу
  };

  render() {
    return (
      <div className="categories">
        {this.state.categories.map((category, index) => (
          <div
            key={index}
            onClick={() => this.handleCategoryClick(category.path)}
            className="category-item"
          >
            {category.name}
          </div>
        ))}
      </div>
    );
  }
}

export default withRouter(Categories);
