import React, { useContext } from "react";
import { useParams } from "react-router-dom";
import { CartContext } from "./CartContext";
import './ProductsPage.css';

const products = {
  Audi: {
    A6: [
      {
        id: 1,
        title: "Прошивка Audi A6 1.9TDI EDC15P",
        sku: "AU0A6D0012",
        image: require('../img/Audi_A6.jpg'),
        price: 100,
      },
    ],
    R8: [
      {
        id: 2,
        title: "Прошивка Audi R8 Performance",
        sku: "AU0R8P0023",
        image: require('../img/Audi_A6.jpg'),
        price: 120,
      },
    ],
  },
  BMW: {
    M3: [
      {
        id: 3,
        title: "Прошивка BMW E34 525i 172HP",
        sku: "BWE34P0001",
        image: require('../img/BMW_E34.jpg'),
        price: 110,
      },
    ],
  },
};

function ProductsPage() {
  const { brand, model } = useParams(); // Получаем марку и модель из URL
  const { addToCart } = useContext(CartContext);

  return (
    <div className="products-page">
      <h1>Товары для {brand} {model}</h1>
      <div className="product-list">
        {products[brand]?.[model]?.map((product) => (
          <div className="product-card" key={product.id}>
            <img src={product.image} alt={product.title} className="product-image" />
            <div className="product-info">
              <p className="product-sku">SKU: {product.sku}</p>
              <h2 className="product-title">{product.title}</h2>
              <p className="product-price">{product.price} BYN</p>
            </div>
            <div className="product-buttons">
              <button className="btn-buy" onClick={() => addToCart(product)}>
                Купить
              </button>
              <button className="btn-buy-one-click">Купить в 1 клик</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProductsPage;
