import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import './HomePage.css';

function HomePage() {
  const [brands, setBrands] = useState([]); // Состояние для хранения списка марок
  const [error, setError] = useState(null); // Состояние для ошибок

  // Функция для загрузки марок
  const fetchBrands = async () => {
    try {
      const response = await axios.get("http://localhost:5001/api/brands");
      setBrands(response.data);
    } catch (err) {
      console.error("Ошибка при загрузке марок:", err);
      setError("Не удалось загрузить марки");
    }
  };

  useEffect(() => {
    fetchBrands(); // Загружаем марки при монтировании компонента
  }, []);

  return (
    <div className="homepage">
      <h1 className="title">Марки автомобилей</h1>
      <hr className="polosa" />
      {error && <p className="error">{error}</p>} {/* Показываем ошибку, если есть */}
      <div className="brand-list">
        {brands.map((brand) => (
          <Link to={`/brand/${brand.id}`} key={brand.id}>
            <div className="brand-item">
              <img
                src={`http://localhost:5001/${brand.image}`} // Убедитесь, что поле "image" в базе данных содержит путь к картинке
                className="brand-image"
                alt={brand.title}
              />
              <p className="brand-title">{brand.title}</p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}

export default HomePage;
