import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';

class Categories extends Component {
  constructor(props) {
    super(props);
    this.state = {
      categories: [
        {
          name: 'Все',
          path: '/', // Путь для категории
        },
        {
          name: 'Легковые авто',
          path: '/brand/cars', // Пример пути для легковых авто
        },
        {
          name: 'Мототехника',
          path: '/brand/motorcycles', // Пример пути для мототехники
        },
        {
          name: 'Грузовики',
          path: '/brand/trucks', // Пример пути для грузовиков
        },
      ],
    };
  }

  handleCategoryClick = (path) => {
    // Используем `this.props.navigate` для перехода на указанный путь
    this.props.navigate(path);
  };

  render() {
    return (
      <div className="categories">
        {this.state.categories.map((el, index) => (
          <div
            key={index}
            onClick={() => this.handleCategoryClick(el.path)} // Переход по маршруту
            className="category-item"
          >
            {el.name}
          </div>
        ))}
      </div>
    );
  }
}

// Оборачиваем компонент для доступа к `navigate`
export default withRouter(Categories);
